<script>
import { round } from 'lodash'

export default {
  name: 'SvgVirtualScrollDemo',
  data () {
    return {
      width: 800,
      height: 600,
      scale: 0.5,
      left: 0,
      top: 0
    }
  },
  methods: {
    onMouseWheel (e) {
      console.log('onMouseWheel', e)
      let { container } = this.$refs
      let restY = container.scrollHeight - container.clientHeight - container.scrollTop
      let restX = container.scrollWidth - container.clientWidth - container.scrollLeft
      // 修正比例
      let wheelDeltaY = round(e.deltaY / 150 * 50, 0)
      let wheelDeltaX = round(e.deltaX / 150 * 50, 0)
      // 如果还可以滚动，则执行默认事件
      if (restY < wheelDeltaY) {
        this.top += wheelDeltaY
      } else if (wheelDeltaY + container.scrollTop < 0) {
        this.top += wheelDeltaY + container.scrollTop
      }
      if (maxX === container.scrollLefts || (container.scrollLeft === 0 && e.deltaX < 0)) {
        this.left += wheelDeltaX
      }
    }
  }
}
</script>